mapper.select:
select * from t_video where 1=1
and area = #{area}
order by id desc limit #{start}, #{limit}

mapper.delete:
delete from
    t_video_like
where
    videoId = #{videoId}
    and userId = #{userId}

delete from
    t_video_collection
where
    videoId = #{videoId}
    and userId = #{userId}

mapper.update:
update
    t_video_coin
set
    amount = #{amount},
    updateTime = #{updateTime}
where
    videoId = #{videoId}
  and userId = #{userId}

mapper.select:
select * from t_video_comment where rootId in
#{rootId}
order by id

select
    *
from
    t_video_view
where
    videoId = #{videoId}

        and clientId = #{clientId} and userId is null


        and userId = #{userId}


        and ip = #{ip}


        and DATE_FORMAT(createTime,'%Y-%m-%d') = #{today}

select
*
from
t_video_binary_picture
where
videoId = #{videoId}

    and frameNo = #{frameNo}


    and videoTimestamp = #{videoTimestamp}
